// Parse the incoming alert from Prometheus
const alertData = $input.first().json;

console.log('ğŸš¨ Received Chaos Alert:', JSON.stringify(alertData, null, 2));

// Extract relevant information
const chaosType = alertData.alerts?.[0]?.annotations?.chaos_type || 'unknown';
const severity = alertData.alerts?.[0]?.labels?.severity || 'unknown';
const alertType = alertData.alerts?.[0]?.labels?.alert_type || 'unknown';
const summary = alertData.alerts?.[0]?.annotations?.summary || 'No summary';
const description = alertData.alerts?.[0]?.annotations?.description || 'No description';

// Create structured data for the healing workflow
const healingContext = {
  timestamp: new Date().toISOString(),
  chaos_type: chaosType,
  severity: severity,
  alert_type: alertType,
  summary: summary,
  description: description,
  service_url: 'http://microservice:8080',
  healing_needed: true,
  investigation_prompt: `CHAOS ENGINEERING ALERT DETECTED:

Type: ${chaosType}
Severity: ${severity}
Alert: ${alertType}
Summary: ${summary}
Description: ${description}

Please analyze the microservice code and provide a solution to fix this issue. Focus on the chaos engineering scenario and provide specific code fixes.`
};

console.log('ğŸ”§ Healing Context Created:', JSON.stringify(healingContext, null, 2));

return { json: healingContext };

// Analyze the service status and determine healing strategy
const alertContext = $input.first().json;
const statusData = $input.last().json;

console.log('ğŸ” Service Status:', JSON.stringify(statusData, null, 2));

// Determine healing strategy based on chaos type and current status
let healingStrategy = {
  chaos_type: alertContext.chaos_type,
  active_chaos: statusData.active_chaos || [],
  immediate_action_needed: false,
  cursor_analysis_needed: true,
  healing_endpoints: [],
  investigation_focus: ''
};

// Define healing strategies for different chaos types
switch (alertContext.chaos_type) {
  case 'memory_leak':
    healingStrategy.healing_endpoints = ['/admin/chaos/heal'];
    healingStrategy.investigation_focus = 'Memory management and garbage collection issues';
    healingStrategy.immediate_action_needed = statusData.system_impact?.estimated_memory_usage_mb > 50;
    break;

  case 'slow_responses':
    healingStrategy.healing_endpoints = ['/admin/chaos/heal'];
    healingStrategy.investigation_focus = 'Performance bottlenecks and response time optimization';
    healingStrategy.immediate_action_needed = true;
    break;

  case 'error_injection':
    healingStrategy.healing_endpoints = ['/admin/chaos/heal'];
    healingStrategy.investigation_focus = 'Error handling and resilience patterns';
    healingStrategy.immediate_action_needed = true;
    break;

  case 'cpu_spike':
    healingStrategy.healing_endpoints = ['/admin/chaos/heal'];
    healingStrategy.investigation_focus = 'CPU optimization and resource management';
    healingStrategy.immediate_action_needed = statusData.system_impact?.performance_degraded;
    break;

  default:
    healingStrategy.investigation_focus = 'General system health and stability';
    healingStrategy.immediate_action_needed = true;
}

// Create Cursor AI prompt
const cursorPrompt = `ğŸ”§ CHAOS ENGINEERING HEALING REQUEST

ALERT DETAILS:
- Type: ${alertContext.chaos_type}
- Severity: ${alertContext.severity}
- Summary: ${alertContext.summary}
- Description: ${alertContext.description}

CURRENT SYSTEM STATE:
- Active Chaos: ${JSON.stringify(statusData.active_chaos)}
- Memory Usage: ${statusData.system_impact?.estimated_memory_usage_mb || 0}MB
- Performance Degraded: ${statusData.system_impact?.performance_degraded || false}

INVESTIGATION FOCUS:
${healingStrategy.investigation_focus}

PLEASE ANALYZE AND PROVIDE:
1. Root cause analysis
2. Specific code fixes needed
3. Prevention strategies
4. Testing recommendations

CODE REPOSITORY: /Users/atakanvardar/Desktop/stajdevopsproje
MAIN APPLICATION: app/main.py

Please inspect the code and provide actionable solutions.`;

const result = {
  ...alertContext,
  healing_strategy: healingStrategy,
  cursor_prompt: cursorPrompt,
  service_status: statusData
};

console.log('ğŸ¯ Healing Strategy:', JSON.stringify(result, null, 2));

return { json: result };

// Parse Cursor AI response and prepare healing actions
const workflowData = $input.first().json;
const cursorResponse = $input.last().json;

console.log('ğŸ§  Cursor AI Response:', cursorResponse);

// Extract healing actions from Cursor response
let healingActions = {
  immediate_heal: workflowData.healing_strategy.immediate_action_needed,
  cursor_suggestions: cursorResponse.stdout || 'No suggestions received',
  healing_endpoints: workflowData.healing_strategy.healing_endpoints,
  test_endpoints: [
    '/healthz',
    '/ready', 
    '/api/v1/hello',
    '/metrics'
  ],
  validation_needed: true
};

// Parse Cursor suggestions if they contain structured data
try {
  const suggestions = JSON.parse(cursorResponse.stdout);
  if (suggestions.healing_actions) {
    healingActions = { ...healingActions, ...suggestions.healing_actions };
  }
} catch (e) {
  console.log('Cursor response is not JSON, treating as text');
}

const result = {
  ...workflowData,
  healing_actions: healingActions,
  cursor_analysis: cursorResponse
};

console.log('ğŸ”§ Healing Actions Prepared:', JSON.stringify(result, null, 2));

return { json: result };

// Validate healing success and prepare final report
const workflowData = $input.first().json;
const healingResponse = $input.all()[1].json;
const testResults = $input.all().slice(2); // All test results

console.log('âœ… Healing Response:', JSON.stringify(healingResponse, null, 2));
console.log('ğŸ§ª Test Results:', JSON.stringify(testResults, null, 2));

// Analyze test results
let testsPassedCount = 0;
let testResults_detailed = [];

testResults.forEach((result, index) => {
  const testPassed = result.json && result.json.status !== 'error';
  testsPassedCount += testPassed ? 1 : 0;

  testResults_detailed.push({
    endpoint: workflowData.healing_actions.test_endpoints[index] || `test_${index}`,
    status: testPassed ? 'passed' : 'failed',
    response: result.json
  });
});

const healingSuccess = testsPassedCount >= Math.ceil(testResults.length * 0.8); // 80% success rate

// Create final report
const finalReport = {
  workflow_id: Date.now().toString(),
  timestamp: new Date().toISOString(),
  original_alert: {
    chaos_type: workflowData.chaos_type,
    severity: workflowData.severity,
    summary: workflowData.summary
  },
  healing_performed: {
    actions_taken: healingResponse.actions_taken || [],
    healing_endpoint_called: true,
    immediate_heal_executed: workflowData.healing_actions.immediate_heal
  },
  cursor_analysis: {
    suggestions_received: workflowData.cursor_analysis.stdout ? true : false,
    analysis_summary: workflowData.healing_actions.cursor_suggestions.substring(0, 200) + '...'
  },
  validation_results: {
    tests_total: testResults.length,
    tests_passed: testsPassedCount,
    tests_failed: testResults.length - testsPassedCount,
    success_rate: (testsPassedCount / testResults.length * 100).toFixed(1) + '%',
    detailed_results: testResults_detailed
  },
  overall_status: healingSuccess ? 'success' : 'partial_success',
  recommendations: healingSuccess ? 
    'System successfully healed and all tests passing' : 
    'Partial healing - manual intervention may be required'
};

console.log('ğŸ“Š Final Healing Report:', JSON.stringify(finalReport, null, 2));

return { json: finalReport };
